$("section#slideHome .desktop a img").each(function() {
        var a = 300,
            b = $(this).attr("src");
        $(this)
            .parents("a")
            .attr("style", 'background-image: url("' + b + '"); height: ' + a + "px");
    }),
    $("section#slideHome .slide").slick({
        arrows: !0,
        dots: !0,
        fade: !1,
        infinite: !0,
        speed: 300,
        slidesToShow: 1,
        autoplay: !0,
        autoplaySpeed: 4e3,
        pauseOnHover: !0
    }),
    $("section#slideHome").fadeIn(),
    $("section#topMarcas").length > -1 &&
    ($("section#topMarcas .slider-wrapper").slick({
            slidesToShow: 6,
            slidesToScroll: 2,
            dots: !1,
            arrows: !0,
            infinite: !0,
            autoplay: !0,
            autoplaySpeed: 2e3,
            responsive: [{
                    breakpoint: 1680,
                    settings: {
                        slidesToShow: 6,
                        slidesToScroll: 2
                    }
                },
                {
                    breakpoint: 1280,
                    settings: {
                        slidesToShow: 6,
                        slidesToScroll: 2
                    }
                },
                {
                    breakpoint: 900,
                    settings: {
                        slidesToShow: 3,
                        slidesToScroll: 2
                    }
                },
                {
                    breakpoint: 600,
                    settings: {
                        slidesToShow: 2,
                        slidesToScroll: 1
                    }
                }
            ]
        }),
        $("section#topMarcas").fadeIn());