 function verificaLetra(a) {
     var b = a.charAt(0),
         c = b.toLowerCase();
     return c
 }

 function verificaNum(a) {
     var b = /^[0-9]+$/;
     return b.test(a)
 }

 function appendMarcasLetra(a, b) {
     var c = "#" + a + " ul",
         d = "<li>" + b + "</li>";
     $(c).append(d)
 }

 function appendMarcasNum(a) {
     var b = "<li>" + a + "</li>";
     $("#123 ul").append(b)
 }

